
class Beacon:
    def __init__(self, id, external_id, name, macAddress, zone, radius, widthSegment, heightSegment, color, signalRadius, fadeSpeed, expandSpeed, maxScale, deviceLocation):
        self.id = id
        self.external_id = external_id
        self.name = name
        self.macAddress = macAddress
        self.zone = zone
        self.radius = radius
        self.widthSegment = widthSegment
        self.heightSegment = heightSegment
        self.color = color
        self.signalRadius = signalRadius
        self.fadeSpeed = fadeSpeed
        self.expandSpeed = expandSpeed
        self.maxScale = maxScale
        self.deviceLocation = deviceLocation
        